		String nam = "abba";
		String revv = "";
		for (int i = nam.length() - 1; i >= 0; i--) {
			revv = revv + nam.charAt(i);
		}
		System.out.println(revv);
		if (nam.equals(revv)) {
			System.out.println("Palindrome");
		} else {
			System.out.println("Not a Palindrome");
		}

		
		
		
		
	String name = "abba";
	String rev = "";
		for(int i = name.length() - 1; i >= 0; i--) {
			rev = rev + name.charAt(i);
		}
